function Auth(){this.maskWrapper=$(".mask-wrapper"),this.scrollWrapper=$(".scroll-wrapper")}Auth.prototype.run=function(){var t=this;t.listenShowHideEvent(),t.listenSwitchEvent(),t.listenLoginEvent()},Auth.prototype.showEvent=function(){this.maskWrapper.show()},Auth.prototype.hideEvent=function(){this.maskWrapper.hide()},Auth.prototype.listenShowHideEvent=function(){var t=this,n=$(".auth-box"),e=$(".close-btn");n.click(function(){t.showEvent()}),e.click(function(){t.hideEvent()})},Auth.prototype.listenSwitchEvent=function(){var n=this;$(".switch").click(function(){var t=n.scrollWrapper.css("left");(t=parseInt(t))<0?n.scrollWrapper.animate({left:"0"}):n.scrollWrapper.animate({left:"-400px"})})},Auth.prototype.listenLoginEvent=function(){var t=$(".login"),o=t.find('input[name="telephone"]'),i=t.find('input[name="password"]'),r=t.find('input[name="remember"]');t.find(".submit-btn").click(function(){var t=o.val(),n=i.val(),e=r.prop("checked");sdbuajax.post({url:"/account/login/",data:{telephone:t,password:n,remember:e},success:function(t){console.log("========"),console.log(t),console.log("========")},fail:function(t){console.log(t)}})})},$(function(){(new Auth).run()});