function Auth(){this.maskWrapper=$(".mask-wrapper"),this.scrollWrapper=$(".scroll-wrapper")}Auth.prototype.run=function(){var t=this;t.listenShowHideEvent(),t.listenSwitchEvent(),t.listenLoginEvent()},Auth.prototype.showEvent=function(){this.maskWrapper.show()},Auth.prototype.hideEvent=function(){this.maskWrapper.hide()},Auth.prototype.listenShowHideEvent=function(){var t=this,n=$(".auth-box"),e=$(".close-btn");n.click(function(){t.showEvent()}),e.click(function(){t.hideEvent()})},Auth.prototype.listenSwitchEvent=function(){var n=this;$(".switch").click(function(){var t=n.scrollWrapper.css("left");(t=parseInt(t))<0?n.scrollWrapper.animate({left:"0"}):n.scrollWrapper.animate({left:"-400px"})})},Auth.prototype.listenLoginEvent=function(){var i=this,t=$(".login"),o=t.find('input[name="telephone"]'),r=t.find('input[name="password"]'),s=t.find('input[name="remember"]');t.find(".submit-btn").click(function(){var t=o.val(),n=r.val(),e=s.prop("checked");sdbuajax.post({url:"/account/login/",data:{telephone:t,password:n,remember:e?1:0},success:function(t){if(200==t.code)i.hideEvent(),window.location.reload();else{var n=t.message;if("string"==typeof n||n.constructor==String)console.log(n);else for(var e in n){var o=n[e][0];console.log(o)}}},fail:function(t){console.log(t)}})})},$(function(){(new Auth).run()});