function Banner(){this.bannerGroup=$("#banner-group"),this.index=0,this.leftArrow=$(".left-arrow"),this.rightArrow=$(".right-arrow"),this.bannerUl=$("#banner-ul"),this.liList=this.bannerUl.children("li"),this.bannerCount=this.liList.length,this.listenBannerHover()}Banner.prototype.toggleArrow=function(n){var t=this;n?(t.leftArrow.toggle(),t.rightArrow.toggle()):(t.leftArrow.hide(),t.rightArrow.hide())},Banner.prototype.listenBannerHover=function(){var n=this;this.bannerGroup.hover(function(){clearInterval(n.timer),n.toggleArrow(!0)},function(){n.loop(),n.toggleArrow(!1)})},Banner.prototype.animate=function(){this.bannerUl.animate({left:-800*this.index},1e3)},Banner.prototype.loop=function(){var n=this;n.timer=setInterval(function(){3<=n.index?n.index=0:n.index+=1,n.animate()},2e3)},Banner.prototype.listenArrowClick=function(){var n=this;n.leftArrow.click(function(){0===n.index?n.index=n.bannerCount-1:n.index--,n.animate()}),n.rightArrow.click(function(){n.index===n.bannerCount-1?n.index=0:n.index++,n.animate()})},Banner.prototype.run=function(){this.loop(),this.listenArrowClick()},$(function(){(new Banner).run()});